    plot.figure().clear()
    plot.close()
    plot.cla()
    plot.clf()

    t = np.linspace(0, 1, 1000)
    y=signal.sawtooth(2 * np.pi * (float(40000))* t,0.5)
    plot.plot(t, y )
    plot.title('Se単al de referencia + Se単al Portadora')
    plot.xlabel('Time')
    plot.ylabel('Amplitude')
    plot.grid(True, which='both')
    plot.axhline(y=0, color='k')
    plot.ylim(-1.1, 1.1)
    plot.xlim(0, 0.5)
    y1=float(a)*np.sin((float(50))*t)
