def grafico2(a):  

    plot.figure().clear()
    plot.close()
    plot.cla()
    plot.clf()
    t = np.linspace(0,1,1000)
    y=signal.sawtooth(2 * np.pi * (float(40000))* t,0.5)
    y1=float(a)*np.sin(round(float(50))*t)
    
    data=np.linspace(0,1,1000)
    for i ,value in enumerate(t):
        if (y1[i] > 0):
            if (y1[i] > y[i]):
                data[i]=1
            else: 
                data[i]=-1

        if (y1[i] < 0):
            if (y1[i] < y[i]):
                data[i]=-1
            else: 
                data[i]=1       

    plot.plot(t,data)
    plot.xlabel('Time')
    plot.ylabel('Amplitude')
    plot.grid(True, which='both')
    plot.axhline(y=0, color='k')
    plot.title('Se単al de Salida del Microcontrolador')
    plot.ylim(-1.1, 1.1)
    plot.xlim(0, 0.5)
    plot.savefig('app/static/img/spwm.png')
    return 'app/static/img/spwm.png'

